package view;import java.util.Scanner;/** * Giải phương trình bậc nhất ax + b = 0 . Các hệ số nhập vào từ bàn phím.  * Sử dụng try - catch để bẫy các lỗi khi nhập sai kiểu dữ liệu, ví dụ nhập số nhưng nhập chữ.  * Bẫy lỗi chia 0, trường hợp xảy ra lỗi phải thông báo cho người dùng biết và nhập lại đến khi a b hợp lệ  * thì in ra kết qua x = -b/a */public class Ex01LinearEquationSolver {	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		double a, b;		while (true) {			try {				System.out.print("Enter a: ");				a = Double.parseDouble(sc.nextLine());				System.out.print("Enter b: ");				b = Double.parseDouble(sc.nextLine());				System.out.println("\n" + a + "*x + " + b + " = 0");				System.out.println("x = -" + b + " / " + a + " = " + solveEquation(a, b));				break;			} catch (NumberFormatException | ArithmeticException e) {				e.printStackTrace();				System.out.println("Please enter again !!!");			}		}		sc.close();	}	private static double solveEquation(double a, double b) {		if(a == 0) {			throw new ArithmeticException("'a' must be different from 0 !!!");		}else if(b == 0) {			throw new ArithmeticException("The equation has infinite solutions.");		}		return -b/a;	}}